package codechef;

import java.util.Scanner;

public class Increasing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int t=sc.nextInt();
		while(t-->0) {
			int n=sc.nextInt();
			sc.nextLine();
			String s=sc.nextLine();
			int x=s.charAt(0);
			System.out.println(x);
		}

	}

}
